import {Content} from './Content';
import React, { useContext } from 'react'
import { textContext } from './textContext';
export const Context = () => {
    let text = useContext(textContext)

    return (
      <textContext.Provider>
        <Content>{text}</Content>
      </textContext.Provider>
    );

}
